    `user_id` INT,
    `team_id` INT DEFAULT NULL,
    `event_id` INT DEFAULT NULL,

    PRIMARY KEY (`id`),
    
    CONSTRAINT `FK_subscriptions_user`
    FOREIGN KEY (`user_id`)
    REFERENCES `users`(`user_id`),

